import React, { useState } from 'react';
import './Navbar.css';
import logo from '../Assets/logo.png';
import cart_icon from '../Assets/cart_icon.png';
import { Link } from 'react-router-dom';

const Navbar = () => {
    const [menu, setMenu] = useState("Shop");

    return (
        <div className='navbar'>
            <div className='nav-logo'>
                <img src={logo} alt='logo' />
                <p>SHOPPER</p>
            </div>
            <ul className='nav-menu'>
                <li onClick={() => setMenu("Shop")}><Link to='/' >Shop</Link> {menu === "Shop" ? <hr /> : null}</li>
                <li onClick={() => setMenu("Mens")}><Link to='/mens' >Men</Link> {menu === "Mens" ? <hr /> : null}</li>
                <li onClick={() => setMenu("Womens")}><Link to='/women' >Women</Link> {menu === "Womens" ? <hr /> : null}</li>
                <li onClick={() => setMenu("Kids")}><Link to='/' >Kids</Link>{menu === "Kids" ? <hr /> : null}</li>
            </ul>
            <div className="nav-login-cart">
                <button>Login</button>
                <img src={cart_icon} alt="cart icon" />
                <div className="nav-cart-count">0</div>
            </div>
        </div>
    );
};

export default Navbar;
